import React, { useState, useEffect } from "react";
import axios from "axios";
import "./App.css";
import {
  Button,
  Card,
  Box,
  Typography,
  CircularProgress,
  Alert,
} from "@mui/material";

import PokemonAbilities from "./Components/PokemonAbilites";
import HomePage from "./Components/HomePage";

function App() {
  const [name, setName] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      const response = await axios.get("http://localhost:5001/pokemon/");
      setName(response.data.data);
    };
    fetchData();
  }, []);
  return (
    <div className="App">
      <Box
        sx={{
          display: "flex",
          flexDirection: "column",
          alignItems: "center",
          padding: "20px",
        }}
      >
        {error ? (
          <Alert severity="error">{error.message}</Alert>
        ) : loading ? (
          <CircularProgress />
        ) : (
          <Box
            className="card-grid"
            sx={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
              gap: "16px",
              width: "100%",
              maxWidth: "1200px",
            }}
          >
            {data?.map((i, index) => (
              <Card key={index} sx={{ borderRadius: "8px", boxShadow: 3 }}>
                <CardContent>
                  <Typography variant="h6" component="div">
                    {i.name.toUpperCase()} <br />
                    <Button
                      variant="outlined"
                      onClick={() => (window.location.href = i.url)}
                    >
                      Click here
                    </Button>
                  </Typography>
                </CardContent>
              </Card>
            ))}
          </Box>
        )}
        <Box
          className="button-group"
          sx={{
            display: "flex",
            alignItems: "center",
            gap: "10px",
            marginTop: "20px",
          }}
        >
          <Button
            variant="contained"
            color="primary"
            onClick={decrease}
            disabled={offset <= 0}
            sx={{ minWidth: "100px" }}
          >
            Previous
          </Button>
          <Typography variant="body1" className="offset-display"></Typography>
          <Button
            variant="contained"
            color="primary"
            onClick={increase}
            sx={{ minWidth: "100px" }}
          >
            Next
          </Button>
        </Box>
      </Box>
    </div>
  );
}
export default App;
