import React, { useState, useEffect } from "react";
import axios from "axios";

import PokemonImage from "./Components/PageComponents/PokemonImage";
import Error from "./Components/ElementComponents/Error";
import ToggleColorMode from "./Components/ElementComponents/DarkMode";

import {
  Box,
  Card,
  Typography,
  CardContent,
  Button,
  CircularProgress,
  TextField,
} from "@mui/material";

function App() {
  const [pokemonList, setPokemonList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState("");
  const [limit, setLimit] = useState(10);
  const offset = 0;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5001/pokemon/?offset=${offset}&limit=${limit}`
        );
        setPokemonList(response.data.data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [limit]);

  const filterPokemon = pokemonList.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div
      style={{
        minHeight: "100vh",
        display: "flex",
        flexDirection: "column",
        alignItems: "center",
      }}
    >
      <Box
        component="form"
        sx={{
          "& > :not(style)": { m: 1, width: "104ch" },
          textAlign: "center",
        }}
        noValidate
        autoComplete="off"
      >
        <ToggleColorMode />
        <Button
          sx={{
            color: "#323335",
            backgroundColor: (theme) => theme.palette.primary.main,
            "&:hover": {
              backgroundColor: (theme) => theme.palette.primary.dark,
            },
          }}
        >
          Submit
        </Button>

        <Typography variant="h1" sx={{ color: "#323335", textAlign: "center" }}>
          Welcome to the Pokemon Information Center
        </Typography>
        <TextField
          style={{ backgroundColor: "#eeeee4", marginBottom: "50px" }}
          label="Search Your Pokemon Here"
          variant="filled"
          onChange={(e) => setSearch(e.target.value)}
          value={search}
        />
        <Button
          variant="outlined"
          onClick={() => setLimit(limit + 10)}
          style={{ marginBottom: "30px", maxWidth: "500px" }}
        >
          Load More...
        </Button>
      </Box>

      {error ? (
        <Error message={error} />
      ) : loading ? (
        <CircularProgress />
      ) : (
        <Box
          sx={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
            gap: "16px",
            width: "100%",
            maxWidth: "1200px",
            margin: "auto",
          }}
        >
          {filterPokemon.map((pokemon, index) => (
            <Button
              key={index}
              onClick={() => console.log(`I was clicked ${index}`)}
            >
              <Card sx={{ minWidth: 200 }}>
                <CardContent>
                  <Typography
                    variant="h6"
                    component="div"
                    sx={{ marginBottom: "30px" }}
                  >
                    {pokemon.name.toUpperCase()}
                  </Typography>
                  <PokemonImage pokemonName={pokemon.name} />
                </CardContent>
              </Card>
            </Button>
          ))}
        </Box>
      )}

      <div
        style={{ display: "flex", justifyContent: "center", marginTop: "20px" }}
      >
        <Button
          variant="outlined"
          onClick={() => setLimit(limit + 10)}
          style={{ marginBottom: "30px", maxWidth: "500px" }}
        >
          Load More...
        </Button>
      </div>
    </div>
  );
}

export default App;
