import React, { useState, useEffect } from "react";
import axios from "axios";

import PokemonImage from "./Components/PageComponents/PokemonImage";
import Error from "./Components/ElementComponents/Error";
import {
  Box,
  Card,
  Typography,
  CardContent,
  Button,
  Stack,
  CircularProgress,
  TextField,
} from "@mui/material";

function App() {
  const [pokemonList, setPokemonList] = useState([]);
  const [offset, setOffset] = useState(0);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState("");
  const limit = 10;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5001/pokemon/?offset=${offset}&limit=${limit}`
        );
        setPokemonList(response.data.data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [offset]);
  const filterPokemon = pokemonList.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div
      className="App"
      style={{
        backgroundColor: "GrayText",
        minHeight: "100vh",
        alignItems: "center",
      }}
    >
      <Box
        component="form"
        sx={{ "& > :not(style)": { m: 1, width: "104ch" } }}
        noValidate
        autoComplete="off"
      >
        <TextField
          style={{ backgroundColor: "#ffffff rgba(0,0,0,0.1)", color: "white" }}
          label="Search Your Pokemon Here"
          variant="outlined"
          onChange={(prevValue) => setSearch(prevValue.target.value)}
          value={search}
        />
      </Box>

      {error ? (
        <Error message={error} />
      ) : loading ? (
        <CircularProgress />
      ) : (
        <Box
          sx={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
            gap: "16px",
            width: "100%",
            maxWidth: "1200px",
            margin: "auto",
          }}
        >
          {filterPokemon?.map((pokemon, pokemonIndex) => (
            <Button key={pokemonIndex}>
              <Card sx={{ maxWidth: 200 }}>
                <CardContent>
                  <Typography variant="h6" component="div">
                    {pokemon.name.toUpperCase()}
                  </Typography>
                  <PokemonImage pokemonName={pokemon.name} />
                </CardContent>
              </Card>
            </Button>
          ))}
        </Box>
      )}

      <div
        style={{ display: "flex", justifyContent: "center", marginTop: "20px" }}
      >
        <Stack direction="row" spacing={2}>
          <Button
            variant="contained"
            onClick={(prev) => setOffset(offset - 10)}
            disabled={offset <= 0}
          >
            Prev
          </Button>
          <Button
            variant="contained"
            onClick={(reset) => setOffset(0)}
            disabled={offset <= 0}
          >
            Reset
          </Button>
          <Button
            variant="contained"
            onClick={(next) => setOffset(offset + 10)}
          >
            Next
          </Button>
        </Stack>
      </div>
    </div>
  );
}

export default App;
