import React, { useState, useEffect } from "react";
import axios from "axios";

import Error from "./Error";
import PokemonImage from "../PageComponents/PokemonImage";

import {
  Box,
  CircularProgress,
  TextField,
  Button,
  Card,
  CardContent,
  Typography,
} from "@mui/material";

function SearchPokemon(name) {
  const [pokemonList, setPokemonList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState("");

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5001/pokemon/?name=${name}`
        );
        setPokemonList(response.data.data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [name]);
  const filterPokemon = pokemonList.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <div
      style={{
        backgroundColor: "#eff4f9",
        backgroundAttachment: "fixed",
        minHeight: "100vh",
        alignItems: "center",
        justifyContent: "center",
      }}
    >
      <Box
        style={{
          display: "flex",
          flexDirection: "column",
          justifyContent: "center",
          alignItems: "center",
        }}
        component="form"
        sx={{ "& > :not(style)": { m: 1, width: "104ch" } }}
        noValidate
        autoComplete="off"
      >
        <TextField
          style={{
            backgroundColor: "#eeeee4 ",
            marginBottom: "50px",
          }}
          label="Search Your Pokemon Here"
          variant="filled"
          onChange={(prevValue) => setSearch(prevValue.target.value)}
          value={search}
        />
      </Box>
      {error ? (
        <Error message={error} />
      ) : loading ? (
        <CircularProgress />
      ) : (
        <Box
          sx={{
            display: "grid",
            gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
            gap: "16px",
            width: "100%",
            maxWidth: "1200px",
            margin: "auto",
          }}
        >
          {filterPokemon.map((pokemon, pokemonIndex) => (
            <Button>
              <Card sx={{ minWidth: 200 }}>
                <CardContent>
                  <Typography
                    variant="h6"
                    component="div"
                    style={{ marginBottom: "30px" }}
                  >
                    {pokemon.name.toUpperCase()}
                  </Typography>
                  <PokemonImage pokemonName={pokemon.name} />
                </CardContent>
              </Card>
            </Button>
          ))}
        </Box>
      )}

      <div
        style={{ display: "flex", justifyContent: "center", marginTop: "20px" }}
      ></div>
    </div>
  );
}

export default SearchPokemon;
