import React, { useState, useEffect } from "react";
import axios from "axios";
import { Stack, Button, Card, Box, Grid, Typography } from "@mui/material";
const HomePage = () => {
  const [data, setData] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [offset, setOffset] = useState(0);
  const limit = 10;
  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5001/pokemon/?offset=${offset}&limit=${limit}`
        );
        setData(response.data.data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [offset]);

  const next = () => {
    setOffset(offset + 10);
  };

  const previous = () => {
    setOffset(offset - 10);
  };

  return (
    <div style={{ display: "block", alignItems: "center" }}>
      <>
        {error ? (
          error.message
        ) : loading ? (
          <p>loading</p>
        ) : (
          data.map((i, index) => (
            <Box sx={{ flexGrow: 2 }}>
              <Grid container spacing={2}>
                <Grid size={{ xs: 6, md: 4 }}>
                  <Card
                    key={index}
                    sx={{ minWidth: 275, marginBottom: "30px" }}
                  >
                    <Typography variant="h5" component="div">
                      {i.name}
                    </Typography>
                  </Card>
                </Grid>
              </Grid>
            </Box>
          ))
        )}
      </>

      <div
        style={{ display: "flex", justifyContent: "center", marginTop: "20px" }}
      >
        <Stack direction="row" spacing={2}>
          <Button variant="contained" onClick={previous} disabled={offset <= 0}>
            Prev
          </Button>

          <Button variant="contained" onClick={next}>
            Next
          </Button>
        </Stack>
      </div>
    </div>
  );
};

export default HomePage;
