import React, { useState, useEffect } from "react";
import axios from "axios";

import PokemonImage from "./Components/PageComponents/PokemonImage";

import Error from "./Components/ElementComponents/Error";

import RadioGroup from "@mui/material/RadioGroup";
import Radio from "@mui/material/Radio";
import FormControl from "@mui/material/FormControl";
import FormControlLabel from "@mui/material/FormControlLabel";
import FormLabel from "@mui/material/FormLabel";
import {
  ThemeProvider,
  createTheme,
  useColorScheme,
} from "@mui/material/styles";
import {
  Box,
  Card,
  Typography,
  CardContent,
  Button,
  CircularProgress,
  TextField,
} from "@mui/material";

function App() {
  const [pokemonList, setPokemonList] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  const [search, setSearch] = useState("");
  const [limit, setLimit] = useState(10);
  const offset = 0;

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get(
          `http://localhost:5001/pokemon/?offset=${offset}&limit=${limit}`
        );
        setPokemonList(response.data.data);
      } catch (error) {
        setError(error);
      } finally {
        setLoading(false);
      }
    };
    fetchData();
  }, [limit]);
  const filterPokemon = pokemonList.filter((pokemon) =>
    pokemon.name.toLowerCase().includes(search.toLowerCase())
  );

  return (
    <Box
      sx={{
        display: "flex",
        width: "100%",
        alignItems: "center",
        justifyContent: "center",
        bgcolor: "background.default",
        color: "text.primary",
        borderRadius: 1,
        p: 3,
        minHeight: "56px",
      }}
    >
      <div
        style={{
          minHeight: "100vh",
          alignItems: "center",
          justifyContent: "center",
        }}
      >
        <Box
          style={{
            display: "flex",
            flexDirection: "column",
            justifyContent: "center",
            alignItems: "center",
          }}
          component="form"
          sx={{ "& > :not(style)": { m: 1, width: "104ch" } }}
          noValidate
          autoComplete="off"
        >
          <Typography>
            <h1 style={{ color: "#323335", textAlign: "center" }}>
              Welcome to the Pokemon Information Center
            </h1>
          </Typography>
          <TextField
            style={{
              backgroundColor: "#eeeee4 ",
              marginBottom: "50px",
            }}
            label="Search Your Pokemon Here"
            variant="filled"
            onChange={(prevValue) => setSearch(prevValue.target.value)}
            value={search}
          />
          <Button
            variant="outlined"
            onClick={(loadMore) => setLimit(limit + 10)}
            style={{
              marginBottom: "30px",
              maxWidth: "500px",
            }}
          >
            Load More...
          </Button>
        </Box>

        {error ? (
          <Error message={error} />
        ) : loading ? (
          <CircularProgress />
        ) : (
          <Box
            sx={{
              display: "grid",
              gridTemplateColumns: "repeat(auto-fill, minmax(200px, 1fr))",
              gap: "16px",
              width: "100%",
              maxWidth: "1200px",
              margin: "auto",
            }}
          >
            {filterPokemon?.map((pokemon, pokemonIndex) => (
              <Button
                key={pokemonIndex}
                onClick={() => console.log(`I was clicked ${pokemonIndex}`)}
              >
                <Card sx={{ minWidth: 200 }}>
                  <CardContent>
                    <Typography
                      variant="h6"
                      component="div"
                      style={{ marginBottom: "30px" }}
                    >
                      {pokemon.name.toUpperCase()}
                    </Typography>
                    <PokemonImage pokemonName={pokemon.name} />
                  </CardContent>
                </Card>
              </Button>
            ))}
          </Box>
        )}

        <div
          style={{
            display: "flex",
            justifyContent: "center",
            marginTop: "20px",
          }}
        >
          <Button
            variant="outlined"
            onClick={(loadMore) => setLimit(limit + 10)}
            style={{
              marginBottom: "30px",
              maxWidth: "500px",
            }}
          >
            Load More...
          </Button>
        </div>
      </div>
      <FormControl>
        <FormLabel id="demo-theme-toggle">Theme</FormLabel>
        <RadioGroup
          aria-labelledby="demo-theme-toggle"
          name="theme-toggle"
          row
          value={mode}
          onChange={(event) => setMode(event.target.value)}
        >
          <FormControlLabel value="system" control={<Radio />} label="System" />
          <FormControlLabel value="light" control={<Radio />} label="Light" />
          <FormControlLabel value="dark" control={<Radio />} label="Dark" />
        </RadioGroup>
      </FormControl>
    </Box>
  );
}

export default App;
