import React, { useState, useEffect } from "react";
import axios from "axios";

const PokemonAbilities = ({ pokemonName }) => {
  const [pokemonAbilites, setPokemonAbilities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
    try {
      const fetchAbilities = async () => {
        const response = await axios.get(
          `http://localhost:5001/pokemon/abilites/?name=${pokemonName}`
        );
        setPokemonAbilities(response.data.abilities.sprites);
        console.log(response.data.abilities.sprites.front_default);
      };
      fetchAbilities();
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  }, [pokemonName]);

  return (
    <div>
      {error ? (
        <p>{error.message}</p>
      ) : loading ? (
        <p>loading...</p>
      ) : (
        <img src={pokemonAbilites.front_default} alt="pokemonPic"></img>
      )}
    </div>
  );
};

export default PokemonAbilities;
