import React, { useState, useEffect } from "react";
import axios from "axios";

const PokemonAbilities = ({ pokemonName }) => {
  const [PokemonAbilites, setPokemonAbilities] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);
  useEffect(() => {
    try {
      const fetchAbilities = async () => {
        const response = await axios.get(
          `http://localhost:5001/pokemon/abilities/?name=${pokemonName}`
        );
        setPokemonAbilities(response.data.abilities);
      };
      fetchAbilities();
    } catch (error) {
      setError(error);
    } finally {
      setLoading(false);
    }
  }, [pokemonName]);
  console.log(PokemonAbilites);

  return (
    <div>
      {error ? (
        <p>{error.message}</p>
      ) : loading ? (
        <p>loading...</p>
      ) : (
        PokemonAbilites?.map((i) => <img src={i.sprites.front_default}></img>)
      )}
      }
    </div>
  );
};

export default PokemonAbilities;
