import React, { useState, useEffect } from "react";
import axios from "axios";

const PokemonAbilities = ({ pokemonName }) => {
  const [PokemonAbilites, setPokemonAbilities] = useState([]);
  const [loading, setLoading] = useState(false);
  const [error, setError] = useState(null);
  useEffect(() => {
    try {
      setLoading(true);
      const fetchAbilities = async () => {
        const response = await axios.get(
          `http://localhost:5001/pokemon/abilities/?name=${pokemonName}`
        );
        setPokemonAbilities(response.data.abilities);
        console.log(response.data.abilities);
      };
      fetchAbilities();
      setLoading(false);
    } catch (error) {
      setError(error);
    }
  }, [pokemonName]);

  return (
    <div>
      {error ? (
        <p>{error.message}</p>
      ) : loading ? (
        <p>loading...</p>
      ) : (
        <img src={PokemonAbilities?.sprites?.front_default} alt="PokemonPic">
          {" "}
        </img>
      )}
    </div>
  );
};

export default PokemonAbilities;
